// Parse CSV data - Phone-first approach
const inputData = $input.all()[0];
let csvData = '';

if (inputData.binary && inputData.binary.data) {
  csvData = Buffer.from(inputData.binary.data.data, 'base64').toString('utf8');
} else if (inputData.json && inputData.json.body && inputData.json.body.csv) {
  csvData = inputData.json.body.csv;
} else {
  throw new Error('No CSV data found in upload');
}

// Parse CSV to JSON - Phone is required, name/email optional
function parseCSV(csvText) {
  const lines = csvText.trim().split('\n');
  if (lines.length < 2) {
    throw new Error('CSV must have at least header and one data row');
  }

  const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
  const leads = [];

  for (let i = 1; i < lines.length; i++) {
    const values = lines[i].split(',').map(v => v.trim());
    if (values.length !== headers.length) continue;

    const lead = {};
    headers.forEach((header, index) => {
      let fieldName = header;
      if (header.includes('name')) fieldName = 'name';
      else if (header.includes('phone') || header.includes('mobile')) fieldName = 'phone';
      else if (header.includes('email')) fieldName = 'email';
      else if (header.includes('note') || header.includes('comment')) fieldName = 'notes';

      lead[fieldName] = values[index] || '';
    });

    // Phone is required, name/email optional
    if (lead.phone) {
      leads.push({
        phone: lead.phone,
        name: lead.name || '', // Optional
        email: lead.email || '', // Optional
        notes: lead.notes || ''
      });
    }
  }

  return leads;
}

const leads = parseCSV(csvData);

return {
  body: {
    leads: leads,
    source: 'CSV Upload',
    uploadedAt: new Date().toISOString()
  }
};

// Process Google Sheets data - Phone-first approach
const inputData = $input.all();

if (!inputData || inputData.length === 0) {
  return { body: { leads: [], source: 'Google Sheets', message: 'No new leads found' } };
}

const leads = [];
const processedRows = [];

inputData.forEach(row => {
  if (row.json && row.json.processed !== 'true') {
    const phone = row.json.phone || row.json.Phone || row.json.mobile || row.json.Mobile || '';

    // Phone is required, name/email optional
    if (phone) {
      const lead = {
        phone: phone,
        name: row.json.name || row.json.Name || '', // Optional
        email: row.json.email || row.json.Email || '', // Optional
        notes: row.json.notes || row.json.Notes || row.json.comments || row.json.Comments || ''
      };

      leads.push(lead);
      processedRows.push(row.json.row_number || leads.length);
    }
  }
});

return {
  body: {
    leads: leads,
    source: 'Google Sheets',
    processedRows: processedRows,
    timestamp: new Date().toISOString()
  }
};

// Handle manual import trigger - fetch Google Sheets data
const triggerData = $input.all()[0]?.json?.body;

if (!triggerData) {
  throw new Error('No trigger data received');
}

// Manual trigger - return signal to fetch Google Sheets
return {
  body: {
    leads: [],
    source: 'Manual Trigger',
    action: 'fetch_sheets',
    timestamp: new Date().toISOString()
  }
};

// Phone-first lead processing with E.164 normalization
const inputData = $input.all()[0].json.body;
const leads = inputData.leads || [];
const importSource = inputData.source || 'Manual';
const timestamp = new Date().toISOString();

// Phone normalization to E.164 format
function normalizePhoneToE164(phone) {
  if (!phone) return null;
  const cleaned = phone.replace(/[^\d+]/g, '');

  // Handle various Indian phone number formats
  if (cleaned.startsWith('+91')) {
    const number = cleaned.substring(3);
    if (number.length === 10 && /^[6-9]\d{9}$/.test(number)) {
      return '+91' + number;
    }
  } else if (cleaned.startsWith('91')) {
    const number = cleaned.substring(2);
    if (number.length === 10 && /^[6-9]\d{9}$/.test(number)) {
      return '+91' + number;
    }
  } else if (cleaned.length === 10 && /^[6-9]\d{9}$/.test(cleaned)) {
    return '+91' + cleaned;
  }

  return null; // Invalid phone number
}

// Calculate data completeness percentage
function calculateDataCompleteness(phone, name, email) {
  let score = 0;
  if (phone) score += 33; // Phone is required
  if (name && name.trim()) score += 33; // Name is optional
  if (email && email.trim()) score += 34; // Email is optional
  return Math.min(score, 100);
}

// Process leads with phone-first approach
const validLeads = [];
const invalidLeads = [];
const duplicateMap = new Map();

leads.forEach((lead, index) => {
  const normalizedPhone = normalizePhoneToE164(lead.phone);

  if (!normalizedPhone) {
    invalidLeads.push({
      index: index,
      originalData: lead,
      errors: ['Invalid Indian mobile number format']
    });
    return;
  }

  const processedLead = {
    phone: normalizedPhone,
    name: lead.name ? lead.name.trim() : '',
    email: lead.email ? lead.email.trim().toLowerCase() : '',
    notes: lead.notes || '',
    leadSource: importSource,
    status: 'New',
    dataCompleteness: calculateDataCompleteness(normalizedPhone, lead.name, lead.email),
    interestDetected: false,
    whatsappScore: 0,
    whatsappRating: 1,
    whatsappLastConversation: '[]',
    meetingBooked: false,
    createdAt: timestamp,
    updatedAt: timestamp
  };

  // Check for duplicates by phone
  if (duplicateMap.has(normalizedPhone)) {
    duplicateMap.get(normalizedPhone).push({ index, lead: processedLead });
  } else {
    duplicateMap.set(normalizedPhone, [{ index, lead: processedLead }]);
    validLeads.push({
      index: index,
      normalizedData: processedLead,
      deduplicationKey: normalizedPhone
    });
  }
});

// Handle duplicates within batch
const duplicateLeads = [];
duplicateMap.forEach((leadGroup, phone) => {
  if (leadGroup.length > 1) {
    leadGroup.slice(1).forEach(duplicate => {
      duplicateLeads.push({
        index: duplicate.index,
        normalizedData: duplicate.lead,
        deduplicationKey: phone
      });
    });
  }
});

const summary = {
  total: leads.length,
  valid: validLeads.length,
  invalid: invalidLeads.length,
  duplicates: duplicateLeads.length,
  source: importSource,
  timestamp: timestamp
};

console.log('Phone-first lead processing complete:', summary);

return {
  validLeads: validLeads,
  invalidLeads: invalidLeads,
  duplicateLeads: duplicateLeads,
  summary: summary,
  importSource: importSource,
  timestamp: timestamp
};

// Deduplicate against existing Airtable records - Phone-first approach
const processedData = $input.first().json;
const existingLeads = $input.last().json || [];
const importSource = processedData.importSource;
const timestamp = processedData.timestamp;

// Create map of existing leads by phone
const existingLeadsMap = new Map();
if (Array.isArray(existingLeads)) {
  existingLeads.forEach(lead => {
    if (lead.fields && lead.fields.Phone) {
      existingLeadsMap.set(lead.fields.Phone, lead);
    }
  });
}

// Separate new leads from duplicates
const newLeads = [];
const duplicateUpdates = [];

processedData.validLeads.forEach(leadItem => {
  const lead = leadItem.normalizedData;
  const existingLead = existingLeadsMap.get(lead.phone);

  if (existingLead) {
    // This is a duplicate - append import info to Notes
    const importNote = `\n--- DUPLICATE IMPORT (${timestamp}) ---\n` +
      `Source: ${importSource}\n` +
      `Phone: ${lead.phone}\n` +
      `Name: ${lead.name || 'Not provided'}\n` +
      `Email: ${lead.email || 'Not provided'}\n` +
      `Data Completeness: ${lead.dataCompleteness}%\n` +
      `Original Notes: ${lead.notes || 'None'}\n` +
      `--- END DUPLICATE IMPORT ---`;

    const updatedNotes = (existingLead.fields.Notes || '') + importNote;

    // Update data completeness if new data is more complete
    const currentCompleteness = existingLead.fields.DataCompleteness || 33;
    const newCompleteness = Math.max(currentCompleteness, lead.dataCompleteness);

    duplicateUpdates.push({
      id: existingLead.id,
      fields: {
        Notes: updatedNotes,
        LeadSource: lead.leadSource,
        DataCompleteness: newCompleteness,
        UpdatedAt: timestamp,
        // Update name/email if they were empty and now provided
        ...((!existingLead.fields.Name || existingLead.fields.Name === '') && lead.name ? { Name: lead.name } : {}),
        ...((!existingLead.fields.Email || existingLead.fields.Email === '') && lead.email ? { Email: lead.email } : {})
      }
    });
  } else {
    // This is a new lead - create with phone-first approach
    newLeads.push({
      fields: {
        Phone: lead.phone,
        Name: lead.name || '', // Optional - can be empty
        Email: lead.email || '', // Optional - can be empty
        Status: 'New',
        LeadSource: lead.leadSource,
        Notes: lead.notes || '',
        DataCompleteness: lead.dataCompleteness,
        InterestDetected: false,
        WhatsAppScore: 0,
        WhatsAppRating: '1',
        WhatsAppLastConversation: '[]',
        MeetingBooked: false,
        CreatedAt: timestamp,
        UpdatedAt: timestamp
      }
    });
  }
});

console.log(`Phone-first deduplication complete: ${newLeads.length} new leads, ${duplicateUpdates.length} duplicates`);

return {
  newLeads: newLeads,
  duplicateUpdates: duplicateUpdates,
  summary: {
    ...processedData.summary,
    newLeads: newLeads.length,
    duplicateUpdates: duplicateUpdates.length
  },
  invalidLeads: processedData.invalidLeads,
  importSource: importSource,
  timestamp: timestamp
};

// Generate comprehensive import summary for phone-first approach
const dedupeData = $('Deduplicate Phone Leads').first().json;
const createResult = $('Create Phone Leads').first()?.json || [];
const updateResult = $('Update Duplicate Leads').first()?.json || [];

// Create final summary
const finalSummary = {
  ...dedupeData.summary,
  createdLeads: Array.isArray(createResult) ? createResult.length : 0,
  updatedLeads: Array.isArray(updateResult) ? updateResult.length : 0,
  processingTime: new Date().toISOString(),
  success: true,
  approach: 'phone-first'
};

// Generate detailed report
let report = `ðŸ“± ATC Local Lead Import - Phone-First Summary\n\n`;
report += `Import Source: ${dedupeData.importSource}\n`;
report += `Processing Time: ${finalSummary.processingTime}\n`;
report += `Approach: Phone-first (name/email optional)\n\n`;

report += `ðŸ“ˆ Import Results:\n`;
report += `â€¢ Total Leads Processed: ${finalSummary.total}\n`;
report += `â€¢ âœ… Valid Phone Numbers: ${finalSummary.valid}\n`;
report += `â€¢ ðŸ†• New Leads Created: ${finalSummary.createdLeads}\n`;
report += `â€¢ ðŸ”„ Duplicate Leads Updated: ${finalSummary.updatedLeads}\n`;
report += `â€¢ âŒ Invalid Phone Numbers: ${finalSummary.invalid}\n\n`;

report += `ðŸ“Š Data Completeness Approach:\n`;
report += `â€¢ Phone Only (33%): Ready for WhatsApp outreach\n`;
report += `â€¢ Phone + Name/Email (66%): Partial data available\n`;
report += `â€¢ Phone + Name + Email (100%): Complete profile\n\n`;

// Add invalid lead details if any
if (dedupeData.invalidLeads && dedupeData.invalidLeads.length > 0) {
  report += `âŒ Invalid Phone Numbers Details:\n`;
  dedupeData.invalidLeads.forEach((item, i) => {
    report += `  ${i + 1}. Row ${item.index + 1}: ${item.errors.join(', ')} - Phone: ${item.originalData.phone}\n`;
  });
  report += '\n';
}

report += `âœ… Result: ${finalSummary.createdLeads + finalSummary.updatedLeads} leads ready for WhatsApp AI workflow\n`;
report += `Processing completed at: ${finalSummary.processingTime}`;

console.log('Final Phone-First Import Summary:', finalSummary);

return {
  summary: finalSummary,
  report: report,
  invalidLeads: dedupeData.invalidLeads || [],
  timestamp: dedupeData.timestamp,
  success: true,
  readyForWhatsApp: finalSummary.createdLeads + finalSummary.updatedLeads
};

